tinymce.PluginManager.add('arkbquote', function (editor, url) {    editor.addButton('arkbquote', {        onclick: function () {				editor.selection.setContent('<blockquote>&nbsp;' + editor.selection.getContent() + '</blockquote><p>&nbsp;</p>');         },        tooltip: 'Супер Цитата',        image: url + '/img/cite.png'    })});